import React, { useState, useEffect } from "react";
import NProgress from "nprogress";
import Router from "next/router";
import Link from "next/link";
Router.onRouteChangeStart = (url) => NProgress.start();
Router.onRouteChangeComplete = (url) => NProgress.done();
Router.onRouteChangeError = (url) => NProgress.done();

const Header = () => {
  let listener = null;
  const [scrollState, setScrollState] = useState("top");

  useEffect(() => {
    listener = document.addEventListener("scroll", (e) => {
      var scrolled = document.scrollingElement.scrollTop;
      if (scrolled >= 100) {
        if (scrollState !== "amir") {
          setScrollState("amir");
        }
      } else {
        if (scrollState !== "top") {
          setScrollState("top");
        }
      }
    });
    return () => {
      document.removeEventListener("scroll", listener);
    };
  }, [scrollState]);

  const [mobileMenu, setMobileMenu] = useState(true);

  const menuTrigger = () => {
    setMobileMenu(!mobileMenu);
  };
  const showMenu = {
    display: "block",
  };
  const hidemenu = {
    display: "none",
  };

  return (
    <React.Fragment>
      {/* <div id="preloader">
        <div id="loader" className="spinner">
          <div id="shadow"></div>
          <div id="box"></div>
        </div>
      </div> */}
      <header
        className={
          scrollState === "top" ? "header-area " : "header-area header-sticky"
        }
        style={{
          background:
            scrollState === "top" ? "rgba(0,0,0,0)" : "rgba(255,255,255,1)",
          color: scrollState === "top" ? "white" : "black",
        }}
      >
        <div className="container">
          <div className="row">
            <div className="col-12">
              <nav className="main-nav">
                <a href="orange-index.html" className="logo">
                  <img
                    src="/static/assets/images/logo-orange.png"
                    alt="Turing"
                  />
                </a>
                <ul
                  className="nav"
                  style={mobileMenu === true ? hidemenu : showMenu}
                >
                  <li>
                    <Link>
                      <a href="/">Home</a>
                    </Link>
                  </li>
                  <li className="submenu">
                    <a href="#">Discover</a>
                    <ul>
                      <li>
                        <a href="#">Features</a>
                      </li>
                      <li>
                        <a href="#">Our Team</a>
                      </li>
                      <li>
                        <a href="#">Pricing Plans</a>
                      </li>
                      <li>
                        <a href="#">Latests Blogs</a>
                      </li>
                    </ul>
                  </li>
                  <li>
                    <Link>
                      <a href="/">About Us</a>
                    </Link>
                  </li>
                  <li>
                    <a href="#">Contact</a>
                  </li>
                </ul>
                <a className="menu-trigger" onClick={menuTrigger}>
                  <span>Menu</span>
                </a>
                <ul className="header-buttons">
                  <li>
                    <a className="btn-nav-line" href="#">
                      Login
                    </a>
                  </li>
                  <li>
                    <a className="btn-nav-primary" href="#">
                      Support
                    </a>
                  </li>
                </ul>
              </nav>
            </div>
          </div>
        </div>
      </header>
    </React.Fragment>
  );
};

export default Header;
